section .bss
    ; Reserve space for loop counters or other variables

section .text
; build pixel
mov ah, 00h
mov al, 13h
int 10h

call clearScreen
call printSprite



; we'll reset the screen to text mode
; wait for a keypress
mov ax, 0c01h
int 21h

; set back normal display mode
mov ax, 03
int 10h

; exit to dos
int 20h

clearScreen:

mov ax, 0xA000
mov es, ax
mov di, 0 
mov cx, 200*320
rep stosb
ret 

printSprite:
    mov ax, 0xA000     ; Set segment register to the VGA memory
    mov es, ax
    mov di, 0          ; Start position in VGA memory
    mov si, mazeSprite ; Start position in sprite data
    mov dx, 200        ; Number of lines in the sprite (height)
eachLine:
    mov cx, 320        ; Number of pixels in one line of the sprite (width)
    rep movsb          ; Move each byte of sprite data to video memory
    ; No need to adjust DI here, as we are writing full lines
    dec dx             ; Decrement line counter
    jnz eachLine       ; Jump if not zero (more lines to process)
    ret


